public class Solution {
    public boolean isPalindrome(int x) {
        if(x < 0)
            return false;

        int divider = 1;
        while (x / divider >= 10) {
            divider *= 10;
        }

        int left; int right;
        while (x != 0){
            left = x / divider;
            right = x % 10;

            if(left != right)
                return  false;

            x = (x - left * divider) / 10;
            divider /= 100;

        }
        return true;

    }
}
